export function parseEmoji(txt) {
  if (!txt) {
    return []
  }
  return txt
  .split(/(\[[\u4e00-\u9fff,\uff1f,\w]{1,8}\])/)
  .filter(str => str.length > 0).map(str => {
    let obj = {}
    if (/\[([\u4e00-\u9fff,\uff1f,\w]{1,8})\]/.test(str)) {
      if (emojis[str]) {
        obj.type = 1
        obj.src = emojis[str].url
      } else {
        obj.type = 0
        obj.value = str
      }
    } else {
      obj.type = 0
      obj.value = str
    }
    return obj
  });
}
